import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Product from './Product';
import ProductForm from './ProductForm'; // Importar el nuevo formulario
import './ProductList.css';

const ProductList = () => {
    const [productsByCategory, setProductsByCategory] = useState({});

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await axios.get('http://localhost:8000/api/products/');
            setProductsByCategory(response.data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    return (
        <div className="product-list-container">
            <ProductForm /> {/* Incluir el formulario en la pÃ¡gina */}
            {Object.entries(productsByCategory).map(([categoryId, { category_name, products }]) => (
                <div key={categoryId} className="category-container">
                    <h2>{category_name}</h2>
                    <div className="product-list">
                        {products.map((product) => (
                            <Product key={product.id} product={product} />
                        ))}
                    </div>
                </div>
            ))}
        </div>
    );
};

export default ProductList;
