import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Product from './Product';
import './ProductList.css';

const ProductList = () => {
    const [productsByCategory, setProductsByCategory] = useState({});

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await axios.get('http://localhost:8000/api/products/');
            setProductsByCategory(response.data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    // Asegúrate de que productsByCategory siempre sea un objeto con categorías
    const categories = Object.entries(productsByCategory);

    return (
        <div className="product-list-container">
            {categories.length === 0 ? (
                <p>Loading products...</p>
            ) : (
                categories.map(([categoryId, { category_name, products }]) => (
                    <div key={categoryId} className="category-container">
                        <h2>{category_name}</h2>
                        <div className="product-list">
                            {products.length === 0 ? (
                                <p>No products available</p>
                            ) : (
                                products.map((product) => (
                                    <Product key={product.id} product={product} />
                                ))
                            )}
                        </div>
                    </div>
                ))
            )}
        </div>
    );
};

export default ProductList;
