import React from 'react';
import { Route, BrowserRouter as Router, Routes } from 'react-router-dom';
import Cart from './components/Cart'; // Asegúrate de tener el componente Cart importado
import Header from './components/Header'; // Asegúrate de tener el Header importado
import Home from './components/Home';
import ProductList from './components/ProductList';

const App = () => {
    return (
        <Router>
            <Header /> {/* El Header estará presente en todas las páginas */}
            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/products" element={<ProductList />} />
                <Route path="/cart" element={<Cart />} /> {/* Ruta para el componente Cart */}
            </Routes>
        </Router>
    );
}

export default App;
