// src/components/ProductList.js
import React, { useEffect, useState } from 'react';
import Product from './Product';

const ProductList = () => {
    const [categories, setCategories] = useState([]);
    const [products, setProducts] = useState([]);

    useEffect(() => {
        // Reemplaza esta URL con tu API real
        fetch('/api/products/categories')
            .then(response => response.json())
            .then(data => setCategories(data));

        // Fetch all products (or fetch based on category if needed)
        fetch('/api/products')
            .then(response => response.json())
            .then(data => setProducts(data));
    }, []);

    const categorizedProducts = categories.reduce((acc, category) => {
        acc[category] = products.filter(product => product.category === category);
        return acc;
    }, {});

    return (
        <div className="product-list">
            {Object.keys(categorizedProducts).map(category => (
                <div key={category} className="product-category">
                    <h2>{category}</h2>
                    <div className="products">
                        {categorizedProducts[category].map(product => (
                            <Product
                                key={product.id}
                                name={product.name}
                                image={product.image}
                                description={product.description}
                                price={product.price}
                                onAddToCart={() => handleAddToCart(product)}
                            />
                        ))}
                    </div>
                </div>
            ))}
        </div>
    );
}

const handleAddToCart = (product) => {
    // Aquí puedes implementar la lógica para añadir el producto al carrito
    console.log('Add to cart:', product);
}

export default ProductList;
