import React from 'react';
import { Link } from 'react-router-dom';
import homeImage from '../img-front/img-home.gif'; // Ruta ajustada según la estructura de carpetas
import whatsappLogo from '../img-front/whalogo.png'; // Ruta ajustada según la estructura de carpetas
import './Home.css';

const Home = () => {
    return (
        <div className="home-container">
            <div className="home-image-container">
                <img src={homeImage} alt="Home" className="home-image" />
            </div>
            <div className="home-text">
                <h2>Welcome to Our Store</h2>
                <p>Explore our collection of amazing products.</p>
                <div className="products-button">
                    <Link to="/products" className="custom-button">Shop Now</Link>
                </div>
            </div>
            <div className="whatsapp-button">
                <a href="https://wa.me/1234567890" target="_blank" rel="noopener noreferrer">
                    <img src={whatsappLogo} alt="WhatsApp" className="whatsapp-logo" />
                </a>
            </div>
        </div>
    );
};

export default Home;
