// src/components/ProductList.js

import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Product from './Product';
import './ProductList.css';

const ProductList = () => {
    const [productsByCategory, setProductsByCategory] = useState({});

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await axios.get('http://localhost:8000/api/products/');
            // Organizar productos por categoría
            const organizedProducts = response.data.reduce((acc, product) => {
                const { category_id, category_name } = product;
                if (!acc[category_id]) {
                    acc[category_id] = { category_name, products: [] };
                }
                acc[category_id].products.push(product);
                return acc;
            }, {});
            setProductsByCategory(organizedProducts);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    return (
        <div className="product-list-container">
            {Object.entries(productsByCategory).map(([categoryId, { category_name, products }]) => (
                <div key={categoryId} className="category-container">
                    <h2>{category_name}</h2>
                    <div className="product-list">
                        {products.map((product) => (
                            <Product key={product.id} product={product} />
                        ))}
                    </div>
                </div>
            ))}
        </div>
    );
};

export default ProductList;
